<script>

    $("#form").on("submit",function(e){

        if($("#email").val().length <= 0){
            $("#emailErrorMessage").addClass("show")
            $("#login_emaildiv").addClass("hasError")
            e.preventDefault()
        }else{

            $("#emailErrorMessage").removeClass("show")
            $("#login_emaildiv").removeClass("hasError")


        }

        if($("#password").val().length <= 0){
            $("#passErrorMessage").addClass("show")
            $("#login_passdiv").addClass("hasError")
            e.preventDefault()

        }
        else{
            $("#passErrorMessage").removeClass("show")
            $("#login_passdiv").removeClass("hasError")
        }
    })

</script>
